import json
import os
from datetime import datetime

class Student:
    """Represents a student in the system"""

    def __init__(self, student_id, name, email):
        self.student_id = student_id
        self.name = name
        self.email = email
        self.courses = {}  # {course_name: grade}
        self.enrollment_date = datetime.now().strftime("%Y-%m-%d %H:%M:%S")

    def enroll_in_course(self, course_name):
        """Enroll student in a course"""
        if course_name not in self.courses:
            self.courses[course_name] = None  # No grade yet
            return True
        return False

    def assign_grade(self, course_name, grade):
        """Assign a grade to a course"""
        if course_name in self.courses:
            self.courses[course_name] = grade
            return True
        return False

    def calculate_gpa(self):
        """Calculate student's GPA"""
        if not self.courses:
            return 0.0

        grade_points = {
            'A': 4.0, 'B': 3.0, 'C': 2.0, 'D': 1.0, 'F': 0.0
        }

        total_points = 0
        graded_courses = 0

        for grade in self.courses.values():
            if grade is not None:  # Only count courses with grades
                total_points += grade_points.get(grade, 0)
                graded_courses += 1

        return total_points / graded_courses if graded_courses > 0 else 0.0

    def to_dict(self):
        """Convert student to dictionary for JSON serialization"""
        return {
            'student_id': self.student_id,
            'name': self.name,
            'email': self.email,
            'courses': self.courses,
            'enrollment_date': self.enrollment_date
        }

    @classmethod
    def from_dict(cls, data):
        """Create student from dictionary"""
        student = cls(data['student_id'], data['name'], data['email'])
        student.courses = data['courses']
        student.enrollment_date = data['enrollment_date']
        return student

    def __str__(self):
        return f"Student {self.student_id}: {self.name} ({self.email})"


class StudentManagementSystem:
    """Main system class for managing students"""

    def __init__(self, data_file="students_data.json"):
        self.data_file = data_file
        self.students = {}
        self.load_data()

    def load_data(self):
        """Load student data from file"""
        try:
            if os.path.exists(self.data_file):
                with open(self.data_file, 'r') as file:
                    data = json.load(file)
                    for student_data in data.values():
                        student = Student.from_dict(student_data)
                        self.students[student.student_id] = student
                print(f"Loaded {len(self.students)} students from file.")
            else:
                print("No existing data file found. Starting fresh.")
        except Exception as e:
            print(f"Error loading data: {e}")

    def save_data(self):
        """Save student data to file"""
        try:
            data = {sid: student.to_dict() for sid, student in self.students.items()}
            with open(self.data_file, 'w') as file:
                json.dump(data, file, indent=2)
            print("Data saved successfully.")
        except Exception as e:
            print(f"Error saving data: {e}")

    def add_student(self, student_id, name, email):
        """Add a new student to the system"""
        if student_id in self.students:
            print("Error: Student ID already exists!")
            return False

        self.students[student_id] = Student(student_id, name, email)
        print(f"Student {name} added successfully!")
        return True

    def remove_student(self, student_id):
        """Remove a student from the system"""
        if student_id in self.students:
            student_name = self.students[student_id].name
            del self.students[student_id]
            print(f"Student {student_name} removed successfully!")
            return True
        else:
            print("Error: Student not found!")
            return False

    def enroll_student(self, student_id, course_name):
        """Enroll a student in a course"""
        if student_id in self.students:
            if self.students[student_id].enroll_in_course(course_name):
                print(f"Student enrolled in {course_name} successfully!")
                return True
            else:
                print("Error: Student already enrolled in this course!")
                return False
        else:
            print("Error: Student not found!")
            return False

    def assign_grade(self, student_id, course_name, grade):
        """Assign a grade to a student for a course"""
        if student_id in self.students:
            if self.students[student_id].assign_grade(course_name, grade):
                print(f"Grade {grade} assigned for {course_name} successfully!")
                return True
            else:
                print("Error: Student not enrolled in this course!")
                return False
        else:
            print("Error: Student not found!")
            return False

    def display_student(self, student_id):
        """Display detailed information about a student"""
        if student_id in self.students:
            student = self.students[student_id]
            print("\n" + "="*50)
            print(f"Student ID: {student.student_id}")
            print(f"Name: {student.name}")
            print(f"Email: {student.email}")
            print(f"Enrollment Date: {student.enrollment_date}")
            print(f"GPA: {student.calculate_gpa():.2f}")
            print("\nCourses:")
            for course, grade in student.courses.items():
                grade_display = grade if grade else "No grade"
                print(f"  - {course}: {grade_display}")
            print("="*50)
        else:
            print("Error: Student not found!")

    def display_all_students(self):
        """Display all students in the system"""
        if not self.students:
            print("No students in the system.")
            return

        print("\n" + "="*50)
        print("ALL STUDENTS")
        print("="*50)

        for student in self.students.values():
            print(f"{student.student_id}: {student.name} | GPA: {student.calculate_gpa():.2f} | Courses: {len(student.courses)}")

    def get_student_statistics(self):
        """Display system statistics"""
        total_students = len(self.students)

        if total_students == 0:
            print("No students in the system.")
            return

        # Calculate average GPA
        total_gpa = sum(student.calculate_gpa() for student in self.students.values())
        average_gpa = total_gpa / total_students

        # Count courses per student
        total_courses = sum(len(student.courses) for student in self.students.values())
        average_courses = total_courses / total_students

        print("\n" + "="*50)
        print("SYSTEM STATISTICS")
        print("="*50)
        print(f"Total Students: {total_students}")
        print(f"Average GPA: {average_gpa:.2f}")
        print(f"Total Courses: {total_courses}")
        print(f"Average Courses per Student: {average_courses:.1f}")


def main():
    """Main program interface"""
    system = StudentManagementSystem()

    while True:
        print("\n" + "="*50)
        print("STUDENT MANAGEMENT SYSTEM")
        print("="*50)
        print("1. Add Student")
        print("2. Remove Student")
        print("3. Enroll in Course")
        print("4. Assign Grade")
        print("5. View Student Details")
        print("6. View All Students")
        print("7. View Statistics")
        print("8. Save and Exit")
        print("="*50)

        choice = input("Enter your choice (1-8): ").strip()

        try:
            if choice == "1":
                student_id = input("Enter Student ID: ").strip()
                name = input("Enter Student Name: ").strip()
                email = input("Enter Student Email: ").strip()
                system.add_student(student_id, name, email)

            elif choice == "2":
                student_id = input("Enter Student ID to remove: ").strip()
                system.remove_student(student_id)

            elif choice == "3":
                student_id = input("Enter Student ID: ").strip()
                course_name = input("Enter Course Name: ").strip()
                system.enroll_student(student_id, course_name)

            elif choice == "4":
                student_id = input("Enter Student ID: ").strip()
                course_name = input("Enter Course Name: ").strip()
                grade = input("Enter Grade (A/B/C/D/F): ").strip().upper()
                system.assign_grade(student_id, course_name, grade)

            elif choice == "5":
                student_id = input("Enter Student ID: ").strip()
                system.display_student(student_id)

            elif choice == "6":
                system.display_all_students()

            elif choice == "7":
                system.get_student_statistics()

            elif choice == "8":
                system.save_data()
                print("Thank you for using Student Management System!")
                break

            else:
                print("Invalid choice! Please enter a number between 1-8.")

        except Exception as e:
            print(f"An error occurred: {e}")


# Run the program
if __name__ == "__main__":
    main()
